let laporan_penukaran = $("#laporan_penukaran").DataTable({
	responsive: !0,
	scrollX: !0,
	ajax: readUrl,
	columnDefs: [{ searcable: !1, orderable: !1, targets: 0 }],
	order: [[1, "desc"]],
	columns: [
		{ data: null },
		{ data: "id_pelanggan" },
		{ data: "tanggal" },
		{ data: "tukar_point" },
		{ data: "jumlah_uangkeluar" },
	],
});

function reloadTable() {
	laporan_penukaran.ajax.reload();
}


laporan_penukaran.on("order.dt search.dt", () => {
	laporan_penukaran
		.column(0, { search: "applied", order: "applied" })
		.nodes()
		.each((a, e) => {
			a.innerHTML = e + 1;
		});
});
